import UIKit

class SKYSearchRouter: NSObject,  SKYSearchRouterInput {
    
    // MARK: - Public vars & lets
    
    weak var viewController: UIViewController?
    
    
    // MARK: - Private vars & lets
    
    func showWord(word: APIResponse.SKYWord) {
        let mainStoryboard: UIStoryboard = UIStoryboard(name: "Main", bundle: nil)
        let controller = mainStoryboard.instantiateViewController(withIdentifier: "SKYWordViewController") as! SKYWordViewController
        let presenter = controller.output as! SKYWordPresenter
        presenter.configure(word: word)
        DispatchQueue.main.async {
            self.viewController?.navigationController?.pushViewController(controller, animated: true)
        }
    }

}
